int main() { std::fstream file; int iterator = 0; file.open("steps.txt"); double steps[365]; while (!file.eof()) {double tempSteps; file >> tempSteps; steps[iterator] = tempSteps; ++iterator;} std::cout << "January average steps: " << std::accumulate(steps, steps + 31, 0) / 31.0 << std::endl << "February average steps: " << std::accumulate(steps + 31, steps + 59, 0) / 28.0 << std::endl << "March average steps: " << std::accumulate(steps + 59, steps + 90, 0) / 31.0 << std::endl << "April average steps: " << std::accumulate(steps + 90, steps + 120, 0) / 30.0 << std::endl << "May average steps: " << std::accumulate(steps + 120, steps + 151, 0) / 31.0 << std::endl << "June average steps: " << std::accumulate(steps + 151, steps + 181, 0) / 30.0 << std::endl << "July average steps: " << std::accumulate(steps + 181, steps + 212, 0) / 31.0 << std::endl << "August average steps: " << std::accumulate(steps + 212, steps + 243, 0) / 31.0 << std::endl << "September average steps: " << std::accumulate(steps + 243, steps + 273, 0) / 30.0 << std::endl << "October average steps: " << std::accumulate(steps + 273, steps + 304, 0) / 31.0 << std::endl << "November average steps: " << std::accumulate(steps + 304, steps + 334, 0) / 30.0 << std::endl << "December average steps: " << std::accumulate(steps + 334, steps + 365, 0) / 31.0 << std::endl; return 0;}